// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MonadDeathmatch Smart Contract
 * @dev A daily elimination game where 100 players compete over 30 days
 * Features:
 * - Daily elimination of 3 players
 * - Three-phase reward distribution (10%, 15%, 50%)
 * - Betting system for final winner and top 10
 * - Multiple pools support
 * - Uses native MON instead of MON token
 */

contract MonadDeathmatch {
    struct Pool {
        uint256 id;                    // Unique pool identifier
        address[] participants;        // List of all participants
        address[] eliminatedToday;     // Today's eliminated players
        uint256 totalPool;            // Total pool amount in MON
        uint256 currentDay;           // Current day (1-30)
        uint256 startTime;            // Pool start timestamp
        bool isActive;                // Pool active status
        mapping(address => bool) isParticipant;  // Participant status tracker
    }

    struct BettingConfig {
        uint256 minBetAmount;      // Minimum bet amount (0.1 MON)
        uint256 maxBetAmount;      // Maximum bet amount (10 MON)
        uint256 bettingEndDay;     // Last day for betting (Day 25)
        uint256 maxBetsPerUser;    // Maximum bets per user (3)
    }

    struct UserBetting {
        uint256 betCount;          // Number of bets placed
        uint256 totalBetAmount;    // Total amount bet
        bool hasBetOnTop10;        // Top 10 prediction status
        bool hasBetOnFinal;        // Final winner prediction status
    }

    struct GameState {
        uint256 firstPhaseReward;    // 10% for first phase (Day 10)
        uint256 secondPhaseReward;   // 15% for second phase (Day 20)
        uint256 finalReward;         // 50% for final winner (Day 30)
        uint256 projectCommission;   // 25% platform fee
        address finalWinnerGuess;    // Final winner prediction
    }

    // State Variables
    address public owner;                          // Contract owner
    uint256 public constant ENTRY_FEE = 1 ether;  // 1 MON entry fee
    uint256 public constant MAX_PARTICIPANTS = 100;// Max players per pool
    uint256 public poolCount;                     // Total pools created

    // Mappings
    mapping(uint256 => Pool) public pools;          // Pool ID => Pool data
    mapping(address => uint256[]) public userPools; // User address => Pool IDs
    mapping(uint256 => mapping(address => UserBetting)) public userBetting; // Pool ID => User address => Betting data

    GameState public gameState;
    BettingConfig public bettingConfig;

    // Events
    event ParticipantJoined(address participant);
    event DailyElimination(address eliminated);
    event PhaseWinner(address winner, uint256 reward, uint256 phase);
    event BetPlaced(address better, address predictedWinner, uint256 amount);
    event NewPoolCreated(uint256 poolId, uint256 timestamp);
    event PoolStarted(uint256 poolId, uint256 timestamp);
    event BetPlacedForTop10(address better, address[] predictedTop10, uint256 amount);

    // Only owner can execute
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        createNewPool(); // Create first pool

        // Set reward distributions
        gameState.firstPhaseReward = 10;      // 10%
        gameState.secondPhaseReward = 15;     // 15%
        gameState.finalReward = 50;           // 50%
        gameState.projectCommission = 25;      // 25%

        // Initialize betting configuration
        bettingConfig = BettingConfig({
            minBetAmount: 0.1 ether,  // 0.1 MON
            maxBetAmount: 10 ether,   // 10 MON
            bettingEndDay: 25,        // Betting ends on day 25
            maxBetsPerUser: 3         // Max 3 bets per user
        });
    }

    function createNewPool() public {
        poolCount++;
        Pool storage newPool = pools[poolCount];
        newPool.id = poolCount;
        newPool.isActive = true;
        newPool.startTime = block.timestamp;
        newPool.currentDay = 1;

        emit NewPoolCreated(poolCount, block.timestamp);
    }

    function joinPool(uint256 _poolId) external payable {
        Pool storage pool = pools[_poolId];
        require(pool.isActive, "Pool is not active");
        require(pool.participants.length < MAX_PARTICIPANTS, "Pool is full");
        require(!pool.isParticipant[msg.sender], "Already joined this pool");
        require(msg.value == ENTRY_FEE, "Must send exactly 1 MON");

        pool.participants.push(msg.sender);
        pool.isParticipant[msg.sender] = true;
        pool.totalPool += msg.value;
        userPools[msg.sender].push(_poolId);

        // Create new pool when current one is full
        if(pool.participants.length == MAX_PARTICIPANTS) {
            createNewPool();
        }

        emit ParticipantJoined(msg.sender);
    }

    function eliminateDaily(uint256 _poolId) external onlyOwner {
        Pool storage pool = pools[_poolId];
        require(pool.isActive, "Pool is not active");
        require(pool.participants.length > 0, "No participants");
        require(pool.eliminatedToday.length < 3, "Daily elimination done");

        for(uint i = 0; i < 3 && pool.participants.length > 0; i++) {
            uint256 randomIndex = uint256(
                keccak256(
                    abi.encodePacked(
                        block.timestamp,
                        block.number,
                        pool.participants.length,
                        i
                    )
                )
            ) % pool.participants.length;

            address eliminated = pool.participants[randomIndex];
            pool.eliminatedToday.push(eliminated);

            // Remove from participants array
            pool.participants[randomIndex] = pool.participants[pool.participants.length - 1];
            pool.participants.pop();

            emit DailyElimination(eliminated);
        }

        // Check for phase rewards
        if(pool.currentDay == 10) {
            distributePhaseReward(_poolId, 1);
        } else if(pool.currentDay == 20) {
            distributePhaseReward(_poolId, 2);
        } else if(pool.currentDay == 30) {
            distributePhaseReward(_poolId, 3);
            pool.isActive = false; // Close the pool
        }

        pool.currentDay++;
        delete pool.eliminatedToday;
    }

    function distributePhaseReward(uint256 _poolId, uint256 phase) internal {
        Pool storage pool = pools[_poolId];
        require(pool.participants.length > 0, "No participants");

        uint256 reward;
        if(phase == 1) {
            reward = (pool.totalPool * gameState.firstPhaseReward) / 100;
        } else if(phase == 2) {
            reward = (pool.totalPool * gameState.secondPhaseReward) / 100;
        } else {
            reward = (pool.totalPool * gameState.finalReward) / 100;
            // Separate project commission
            uint256 commission = (pool.totalPool * gameState.projectCommission) / 100;
            payable(owner).transfer(commission);
        }

        // Send reward to the last participant
        address payable winner = payable(pool.participants[0]);
        winner.transfer(reward);

        emit PhaseWinner(winner, reward, phase);
    }

    function betOnFinalWinner(uint256 _poolId, address predictedWinner) external payable {
        Pool storage pool = pools[_poolId];
        UserBetting storage betting = userBetting[_poolId][msg.sender];

        require(pool.isActive, "Pool is not active");
        require(pool.currentDay <= bettingConfig.bettingEndDay, "Betting period ended");
        require(msg.value >= bettingConfig.minBetAmount, "Bet amount too low");
        require(msg.value <= bettingConfig.maxBetAmount, "Bet amount too high");
        require(betting.betCount < bettingConfig.maxBetsPerUser, "Max bets reached");
        require(!betting.hasBetOnFinal, "Already bet on final winner");

        betting.betCount++;
        betting.totalBetAmount += msg.value;
        betting.hasBetOnFinal = true;
        gameState.finalWinnerGuess = predictedWinner;

        emit BetPlaced(msg.sender, predictedWinner, msg.value);
    }

    function betOnTop10(uint256 _poolId, address[] calldata predictedTop10) external payable {
        Pool storage pool = pools[_poolId];
        UserBetting storage betting = userBetting[_poolId][msg.sender];

        require(pool.isActive, "Pool is not active");
        require(pool.currentDay <= bettingConfig.bettingEndDay, "Betting period ended");
        require(msg.value >= bettingConfig.minBetAmount, "Bet amount too low");
        require(msg.value <= bettingConfig.maxBetAmount, "Bet amount too high");
        require(betting.betCount < bettingConfig.maxBetsPerUser, "Max bets reached");
        require(!betting.hasBetOnTop10, "Already bet on top 10");
        require(predictedTop10.length == 10, "Must predict exactly 10 players");

        betting.betCount++;
        betting.totalBetAmount += msg.value;
        betting.hasBetOnTop10 = true;

        emit BetPlacedForTop10(msg.sender, predictedTop10, msg.value);
    }

    // View functions
    function getParticipantsCount(uint256 _poolId) external view returns (uint256) {
        return pools[_poolId].participants.length;
    }

    function isParticipant(uint256 _poolId, address participant) external view returns (bool) {
        return pools[_poolId].isParticipant[participant];
    }

    function getUserPools(address _user) external view returns (uint256[] memory) {
        return userPools[_user];
    }

    function getUserBettingInfo(uint256 _poolId, address _user) external view returns (
        uint256 betCount,
        uint256 totalBetAmount,
        bool hasBetOnTop10,
        bool hasBetOnFinal
    ) {
        UserBetting storage betting = userBetting[_poolId][_user];
        return (
            betting.betCount,
            betting.totalBetAmount,
            betting.hasBetOnTop10,
            betting.hasBetOnFinal
        );
    }

    // Receive function to accept native MON
    receive() external payable {}
}

